sys_variable_value.value:
   answer = ifScript();


   function ifScript() {
	   var cat = current.getValue('u_category');
      if (cat == "Maintenance Incident") {
         return 'Maintenance';
      }
		 else if (cat == "Security Incident") { 
		 return 'Security';
			 
   }     else if (cat == "Health and Safety Incident") {
		 return 'Health and Safety ';
	   
   }     else if (cat == "Scheduled Event") {
		 return 'Scheduled';
   
	}    else if (cat == "Security Status Report") {
		 return 'Security Site Status Reports';
		
	   }else if (cat == "Contractors Status Report") {
		 return 'Contractor Status Reports';
   }
   }

