var answer = [];

var loc = current.opened_by.location.name;

var workflow_name = 'Engen: Electronic Occurrence Book';

var activity_name = 'Depot Manager';
    
var user_list = new GlideRecord('u_engen_app_notif');
user_list.addQuery ('u_activity.u_workflow.name', workflow_name);
user_list.addQuery ('u_activity.u_activity', activity_name);
user_list.addQuery ('u_activity.u_param_1', loc);
//user_list.addQuery ('u_activity.u_param_2', current.variables.u_parking_region);

user_list.query ();
//gs.log ('u_engen_app_notif Record Count: ' + user_list.getRowCount(), 'Engen_Workflow_Script');

while (user_list.next()) {
  // *** The following should be used if an Approval or Notification is to be created ***
  answer.push (user_list.u_user.sys_id);

}

